package com.syntax.Review02;

public class OperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Operators in Java Arithmetic Operators + - / * % Logical Operators == != >= > <
		
		int num=10;
		int num2=10;
		System.out.println(num+num2);
		System.out.println(num>=num2);//num>num2 or num=num f+t=t\
		//whole 0-infinity Negative infinity to Positive infinity 
		
		System.out.println(Long.MAX_VALUE);// Gives us the maximun range
		System.out.println(Long.MIN_VALUE);
		System.out.println(Byte.MAX_VALUE);
		System.out.println(Integer.MAX_VALUE);
		System.out.println(num/num2);
		System.out.println(num%num2);
		
		System.out.println(10/3);
		System.out.println(10%3);
 
		System.out.println(25/4);
		System.out.println(25%4);
		
		System.out.println(10.0/3);
		System.out.println((int)10.0/3); //Narrowing. its converting a double to int 
		System.out.println(10.0*3);

	}

}
