package com.syntax.Review02;

public class StringConCat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	String firstName="Jessica";
	String lastName="Salcedo";
	String fullName=firstName+lastName;
	int num=10;
	int num2=10;
	
	System.out.println(fullName);
	System.out.println(firstName+num);
	System.out.println(firstName+num+num2);// String variable with in variable, Jessica =String 10= int. 
	System.out.println(firstName+(num+num2)); // When you want int to add, you have to put them in () 	

	}

}
