package com.syntax.class02;

public class VariableValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		
		int rose, sunny, pink, orange; // create variables of int type
		rose=40; // assign the value
		sunny=50; // assign the value 
		pink=60; // assign the value 
		orange=70; // assign the value 
		
		System.out.println(rose);//40
		System.out.println(sunny); //50
		System.out.println(pink); // 60
		System.out.println(orange); // 70
		
		
				

	}

}
