package com.syntax.class08;

public class ForLoopExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		        for(int i=1; i<=90; i++) {
					System.out.println(i+" ");
				}
				/*
				 * start point 
				 * end point 
				 * increment/decrement
				 */
				System.out.println();
				//// I need to numbers from 50 to 10
				
				for(int i=60; i>=10; i--) {
					System.out.println(i+" ");
					
				}
				
				System.out.println();
				//what is output
				
				
				for(int i=5; i<=40; i+=5) {
					System.out.println(i+"");
				}
				
				System.out.println("------------------");
				
				// Print numbers from 1 to 100 in 1 line with space
				
				for(int i=1; i<=40; i++) {
					System.out.print(i+"");
					
				}
				

				System.out.println("------------------");
				// Print numbers from 100 - 1
				
				for(int i=100; i>=1; i--) {
					System.out.println(i+"");
				}
				
				//Print even numbers from 20 to 1
				System.out.println();
				for(int a= 20; a>=1; a-=2) {
					System.out.print(a + " ");
				}
				System.out.println("-----------------");
				
				
				for(int i = 21; i<=50; i+=2) {
					System.out.println(i+" ");
				}
			
				
				for (int i = 20; i <50; i++) {
					if(i%2!=0) {
						System.out.print(i+" ");
					}
				}
	
	
	
	}
	
				
				
				
				
				
				
				
				
				

		


	}

